import React, { Fragment } from "react";
//item en la lista
export function TodoItem({ todo, cambiarEstado, eliminar }) {
  const { id, task, desc, completed} = todo;

  return (
    <Fragment>
      <li className="">
        <a href="#">
          <h2>{todo.task}</h2>
          <p>{todo.desc}</p>
        </a>
      </li>
    </Fragment>
  );
}
