import React, { Fragment } from "react";
//item en la lista
export function TodoItem({ todo, cambiarEstado }) {
  const { id, task, completed } = todo;
  const fnCambiarEstado = () => {
    cambiarEstado(id);
  }
  return (
    <Fragment>
      <li className="">
        <a href="#"><input type="checkbox"
          className="form-check-input me-2" onChange={fnCambiarEstado} checked={completed}/>
          <h2>{todo.task}</h2>
        </a>
      </li>
    </Fragment>
  );
}
