import React, { Fragment, useState, useRef } from "react";
import { useEffect } from "react";
import { flushSync } from "react-dom";
import { v4 as uuid } from "uuid";
import { TodoItem } from "./TodoItem";
//funcion que maneja la lista
const KEY = "todolist-todos";

export function TodoList() {
  const [todos, setTodos] = useState([
    { id: 1, task: "Tarea 1", completed: true },
    { id: 2, task: "Tarea 2", completed: true },
    { id: 3, task: "Tarea 3", completed: true },
    { id: 4, task: "Tarea 4", completed: true },
    { id: 5, task: "Tarea 5", completed: true },
    { id: 6, task: "Tarea 6", completed: true },
    { id: 7, task: "Tarea 7", completed: false },
  ]);

  const taskRef = useRef();
  const descRef = useRef();
  const impRef = useRef();
  


/*   /* permite almacenar datos 
  useEffect(() => {
    const storedTodos = JSON.parse(localStorage.getItem(KEY))
    if((storedTodos)){
      setTodos(storedTodos)
    }
  },[])

  useEffect(() => {
    localStorage.setItem(KEY, JSON.stringify(todos))
  },[todos]) */

  const agregarTarea = () => {

    const handleCheck = () => {
      if (impRef.current.checked) {
        return "importante"
      } else {
        return ""
      };
    };
    //para ques se usa current.value?
    const task = taskRef.current.value;
    const desc = descRef.current.value;
    const imp = handleCheck();

    console.log(task);
    //para que utiliza esto??
    if (task === "") return;

    console.log("Agregando tarea...");

    /* Metodo que esta definido por react para operar los elementos */

    setTodos((prevTodos) => {
      const newTask = {
        id: uuid(),
        task: task,
        desc: desc,
        imp: imp
      };

      return [...prevTodos, newTask];
    });
    //que hace esto?
    taskRef.current.value = "";
    descRef.current.value = "";
  };



  const eliminarNota = (id) => {
    const newTodos = todos.filter((todo) => todo.id !== id);
    setTodos(newTodos);
  }



  return (
    <Fragment>
      <h1>Listado de Tareas</h1>
      <div className="input-group my-4">
        <input
          ref={taskRef}
          type="text"
          placeholder="Ingrese una tarea"
          className="form-control"
        />
        <input 
          ref={descRef}
          type="text" 
          placeholder="Descripcion"
          className="form-control"
        />
        <div class="form-check m-2">
          <input class="form-check-input" type="checkbox" value="" id="importante" ref={impRef}/>
          <label class="form-check-label etiqueta" for="importante">
            Importante!
          </label>
        </div>
        <button type="button" onClick={agregarTarea} className="btn btn-success ms-1">
          +
        </button>
      </div>

      <ul className="">
        {/* Investigar que más se puede hacer con el método map */}
        {todos.map((todo) => (
          <TodoItem todo={todo} 
          key={todo.id} 
          eliminar ={eliminarNota}
          ></TodoItem>
        ))}
      </ul>
    </Fragment>
  );
}
